
public class PrintAlphabet {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		for(int i=0;i<26; i++) {
			System.out.print((char)(i+'a')+ " ");
		}
		System.out.println("");
		for(int i=0;i<26; i++) {
			System.out.print((char)(i+'A')+ " ");
		}
	}

}
